options( warn = -1 )
library(ggplot2)
library(gridExtra)
library(grid)
temp = list.files(pattern="release.*")
for (i in 1:length(temp))
{
  assign(paste0("release",i), read.csv(temp[i]))
}

par(mfrow=c(2,2),cex.lab=0.8,cex.main=0.8)

#using ggplot
for(i in 1:30)
{
  release<-get(paste0("release",i))
  input = readline(paste('Press Enter to view "Defect Age" Plot for release',i))
  plot1<-ggplot(data=release,aes(x=numActive,y=Total.num.days))+ geom_point(color='darkblue')+xlab('Number of times Active')+ylab('Total Num of Days')+ggtitle("Total Num of Days Vs. Number of times Active")+theme(axis.text=element_text(size=8),axis.title=element_text(size=10,face="bold"),plot.title = element_text(size = 8, face = "bold", colour="blue", hjust = 0.5))
  plot2<-ggplot(data=release,aes(x=numDaysActive,y=Total.num.days))+ geom_point(color='darkblue')+xlab('Number of days Active')+ylab('Total Num of Days')+ggtitle("Total Num of Days  Vs.  Number of days Active")+theme(axis.text=element_text(size=8),axis.title=element_text(size=10,face="bold"),plot.title = element_text(size = 8, face = "bold", colour="blue", hjust = 0.5))
  plot3<-ggplot(data=release,aes(x=numReopened,y=Total.num.days))+ geom_point(color='darkblue')+xlab('Number of times Reopened')+ylab('Total Num of Days')+ggtitle("Total Num of Days  Vs.  Number of times Reopened")+theme(axis.text=element_text(size=8),axis.title=element_text(size=10,face="bold"),plot.title = element_text(size = 8, face = "bold", colour="blue", hjust = 0.5))
  plot4<-ggplot(data=release,aes(x=LOC,y=Total.num.days))+ geom_point(color='darkblue')+xlab('Lines of Code')+ylab('Total Num of Days')+ggtitle("Total Num of Days  Vs. Lines of Code")+theme(axis.text=element_text(size=8),axis.title=element_text(size=10,face="bold"),plot.title = element_text(size = 8, face = "bold", colour="blue", hjust = 0.5))
  grid.arrange(plot1,plot2,plot3,plot4,ncol=2,top = textGrob(paste("Plot for 'Defect Age' in release",i),gp=gpar(fontsize=13, col="red")))
  input = readline(paste('Press Enter to view "Dev Hours" Plot for release',i))
  plot5<-ggplot(data=release,aes(x=numActive,y=Num.eff.dev.hours))+ geom_point(color='darkblue')+xlab('Number of times Active')+ylab('Eff. Dev Hours')+ggtitle("Eff. Dev Hours  Vs.  Number of times Active")+theme(axis.text=element_text(size=8),axis.title=element_text(size=10,face="bold"),plot.title = element_text(size = 8, face = "bold", colour="blue", hjust = 0.5))
  plot6<-ggplot(data=release,aes(x=numDaysActive,y=Num.eff.dev.hours))+ geom_point(color='darkblue')+xlab('Number of days Active')+ylab('Eff. Dev Hours')+ggtitle("Eff. Dev Hours  Vs.  Number of days Active")+theme(axis.text=element_text(size=8),axis.title=element_text(size=10,face="bold"),plot.title = element_text(size = 8, face = "bold", colour="blue", hjust = 0.5))
  plot7<-ggplot(data=release,aes(x=numReopened,y=Num.eff.dev.hours))+ geom_point(color='darkblue')+xlab('Number of times Reopened')+ylab('Eff. Dev Hours')+ggtitle("Eff. Dev Hours  Vs.  Number of times Reopened")+theme(axis.text=element_text(size=8),axis.title=element_text(size=10,face="bold"),plot.title = element_text(size = 8, face = "bold", colour="blue", hjust = 0.5))
