  plot10<-ggplot(data=release,aes(x=numDaysActive,y=Num.eff.testing.hours))+ geom_point(color='darkblue')+xlab('Number of days Active')+ylab('Eff. Testing Hours')+ggtitle("Eff. Testing Hours  Vs.  Number of days Active")+theme(axis.text=element_text(size=8),axis.title=element_text(size=10,face="bold"),plot.title = element_text(size = 8, face = "bold", colour="blue", hjust = 0.5))
