gg<-ggplot(data=output_dataset,aes(x=NumDaysActive,y=Num_Priot_Low)) + geom_point(color='darkblue')+xlab('Number of days Active')+ylab('Number of Low Prior defects')+ggtitle("Number of Low priority defects  Vs.  Number of days Active")+theme(axis.text=element_text(size=8),axis.title=element_text(size=7,face="bold"),plot.title = element_text(size = 8, face = "bold", colour="blue", hjust = 0.5))
hh<-ggplot(data=output_dataset,aes(x=NumReopened,y=Num_Priot_Low)) + geom_point(color='darkblue')+xlab('Number of times Reopened')+ylab('Number of Low Prior defects')+ggtitle("Number of Low priority defects  Vs.  Number of times Reopened")+theme(axis.text=element_text(size=8),axis.title=element_text(size=7,face="bold"),plot.title = element_text(size = 8, face = "bold", colour="blue", hjust = 0.5))
ii<-ggplot(data=output_dataset,aes(x=Tot_LOC,y=Num_Priot_Low)) + geom_point(color='darkblue')+xlab('Total Lines of Code')+ylab('Number of Low Prior defects')+ggtitle("Number of Low priority defects  Vs.  Total Lines of Code")+theme(axis.text=element_text(size=8),axis.title=element_text(size=7,face="bold"),plot.title = element_text(size = 8, face = "bold", colour="blue", hjust = 0.5))
grid.arrange(ff,gg,hh,ii, ncol=2,top = textGrob("Trend analysis for 'Number of Low Priority Defects' among all the 30 releases",gp=gpar(fontsize=13, col="red")))

